mapper.select:
SELECT (SELECT COUNT(*) FROM `pili`.`p_subscribe` WHERE subscribe_id=#{userId} AND subscribed_id=s.subscribe_id) isSubYou,`subscribe_id`, `subscribed_id`, i.`createTime` , `user_id` userId, `subscribeNum`, `fansNum`, `level`, `experience`, `user_pic` userPic, `up_desc` upDesc,nickName FROM `pili`.`p_subscribe` s,`pili`.`p_user_info` i WHERE s.`subscribe_id`=i.`user_id` AND subscribed_id = #{userId}
limit #{index},#{count}
;

SELECT (SELECT COUNT(*) FROM `pili`.`p_subscribe` WHERE subscribed_id=#{userId} AND subscribe_id=s.subscribed_id) isSubYou,`subscribe_id`, `subscribed_id`, i.`createTime` , `user_id` userId, `subscribeNum`, `fansNum`, `level`, `experience`, `user_pic` userPic, `up_desc` upDesc,nickName FROM `pili`.`p_subscribe` s,`pili`.`p_user_info` i WHERE s.`subscribed_id`=i.`user_id` AND subscribe_id = #{userId}
limit #{index},#{count}
;

SELECT COUNT(id)
FROM p_subscribe
WHERE subscribed_id = #{subscribedId}
  AND subscribe_id = #{subscribeId}

mapper.delete:
delete
from p_subscribe
where subscribed_id = #{subscribedId}
  AND subscribe_id = #{subscribeId}
